mapper.select:
        select
          dev_id, branch_id, branch_name, ip, visit_time, create_time, retain1, retain2, retain3, retain4, retain5, retain6, retain7, retain8, retain9, retain10, id
        from device_info


                and dev_id = #{devId}


                and branch_id = #{branchId}


                and branch_name = #{branchName}


